package JavaBasic;

public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//premitive data types : int, double, char, boolean.  but string is not a data type
		
		//int store only numeric value which are positive and negative.
		int i=10;
		i=20;
		
		//Double store the decimal values
		double d =12.34;
		System.out.println(i+d);
		
		//char : char accept only single value and should be written in single quots
		char a ='c';
		
		//boolean : it uses for to fetch true and false conditions
		boolean b =true;
		boolean c= false;
		
		//String : String is a class, it is not a data type.
		
		String s="Selenium";
		String f ="1000";
		String h="12.38";
	}

}
