package JavaBasic;

public class StringConcatination {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=100;
		int b=200;
		
		String x="Hello, world";
		
		System.out.println(a+b);
		System.out.println(a+b+x);
		System.out.println(x+a+b);
		System.out.println(x+(a+b));

	}

}
